/**
 * Bun.Cookie + Bun.color Integration
 * Color-coded cookie persistence for sports betting dashboards
 */

import { Cookie, CookieMap } from "bun";
import { fdToColor, normalizeColor, getRGBAArray } from "./color-mapping";

// ============================================================================
// COOKIE TYPES AND INTERFACES
// ============================================================================

export interface CookieOptions {
  path?: string;
  httpOnly?: boolean;
  maxAge?: number;
  sameSite?: "strict" | "lax" | "none";
  secure?: boolean;
}

// ============================================================================
// COOKIE COLOR PROFILES
// ============================================================================

export interface ColorProfile {
  hex: string;
  HEX: string;
  hsl: string;
  rgb: string;
  rgba: string;
  lab: string;
  number: number;
  rgbaArray: [number, number, number, number];
}

export interface FDRegimeProfile {
  fd: string;
  hex: string;
  HEX: string;
  hsl: string;
  lab: string;
  css: string;
  intensity: string;
}

// ============================================================================
// BASIC COLOR COOKIE FUNCTIONS
// ============================================================================

/**
 * Set user theme color cookie with automatic normalization
 */
export function setUserThemeCookie(
  response: Response,
  themeColor: string = "#FF5733",
  options: Partial<CookieOptions> = {}
): void {
  const normalizedHex = normalizeColor(themeColor);

  const cookieOptions = {
    path: "/",
    httpOnly: false,
    maxAge: 60 * 60 * 24 * 30, // 30 days
    sameSite: "lax",
    ...options
  };

  const cookie = new Bun.Cookie("userTheme", normalizedHex, cookieOptions).serialize();
  response.headers.append("Set-Cookie", cookie);
}

/**
 * Get and normalize user theme color from cookies
 */
export function getUserThemeCookie(
  request: Request,
  fallback: string = "#808080"
): string {
  const cookies = new Bun.CookieMap(request.headers.get("cookie") || "");
  const raw = cookies.get("userTheme");

  if (!raw) return fallback;

  try {
    return normalizeColor(raw);
  } catch {
    return fallback;
  }
}

// ============================================================================
// MULTI-FORMAT COLOR PROFILE
// ============================================================================

/**
 * Store comprehensive color profile in single cookie
 */
export function setColorProfileCookie(
  response: Response,
  baseColor: string,
  options: Partial<CookieOptions> = {}
): void {
  const c = (format: string) => Bun.color(baseColor, format as any) as any;

  const profile: ColorProfile = {
    hex: c("hex") as string,
    HEX: c("HEX") as string,
    hsl: c("hsl") as string,
    rgb: c("rgb") as string,
    rgba: c("rgba") as string,
    lab: c("lab") as string,
    number: c("number") as number,
    rgbaArray: c("[rgba]") as [number, number, number, number]
  };

  const cookieValue = JSON.stringify(profile);

  const cookieOptions = {
    path: "/",
    maxAge: 60 * 60 * 24 * 7, // 1 week
    httpOnly: false,
    secure: true,
    sameSite: "strict",
    ...options
  };

  let cookie = `colorProfile=${encodeURIComponent(cookieValue)}`;
  if (cookieOptions.path) cookie += `; Path=${cookieOptions.path}`;
  if (cookieOptions.httpOnly) cookie += `; HttpOnly`;
  if (cookieOptions.maxAge) cookie += `; Max-Age=${cookieOptions.maxAge}`;
  if (cookieOptions.sameSite) cookie += `; SameSite=${cookieOptions.sameSite}`;
  if (cookieOptions.secure) cookie += `; Secure`;

  response.headers.append("Set-Cookie", cookie);
}

/**
 * Get color profile from cookies
 */
export function getColorProfileCookie(
  request: Request,
  format: keyof ColorProfile = "HEX"
): string | number | [number, number, number, number] | null {
  const cookies = new Bun.CookieMap(request.headers.get("cookie") || "");
  const raw = cookies.get("colorProfile");

  if (!raw) return null;

  try {
    const profile = JSON.parse(decodeURIComponent(raw)) as ColorProfile;
    return profile[format] || null;
  } catch {
    return null;
  }
}

// ============================================================================
// FD REGIME COLOR-CODED COOKIES
// ============================================================================

/**
 * Store current FD regime color for dashboard restoration
 */
export function setFDRegimeCookie(
  response: Response,
  currentFD: number,
  options: Partial<CookieOptions> = {}
): void {
  const regimeColor = fdToColor(currentFD);
  
  const profile: FDRegimeProfile = {
    fd: currentFD.toFixed(2),
    hex: Bun.color(regimeColor, "hex") as string,
    HEX: Bun.color(regimeColor, "HEX") as string,
    hsl: Bun.color(regimeColor, "hsl") as string,
    lab: Bun.color(regimeColor, "lab") as string,
    css: Bun.color(regimeColor, "css") as string,
    intensity: Math.min(currentFD / 2.8, 1).toFixed(2)
  };
  
  const cookieValue = JSON.stringify(profile);
  
  const cookieOptions = {
    path: "/",
    maxAge: 60 * 15, // 15 minutes - volatility changes fast
    httpOnly: false,
    sameSite: "lax",
    ...options
  };

  let cookie = `fdRegimeColor=${cookieValue}`;
  if (cookieOptions.path) cookie += `; Path=${cookieOptions.path}`;
  if (cookieOptions.httpOnly) cookie += `; HttpOnly`;
  if (cookieOptions.maxAge) cookie += `; Max-Age=${cookieOptions.maxAge}`;
  if (cookieOptions.sameSite) cookie += `; SameSite=${cookieOptions.sameSite}`;
  if (cookieOptions.secure) cookie += `; Secure`;

  response.headers.append("Set-Cookie", cookie);
}

/**
 * Get FD regime color from cookies
 */
export function getFDRegimeCookie(
  request: Request
): FDRegimeProfile | null {
  const cookies = new Bun.CookieMap(request.headers.get("cookie") || "");
  const raw = cookies.get("fdRegimeColor");

  if (!raw) return null;

  try {
    return JSON.parse(raw) as FDRegimeProfile;
  } catch {
    return null;
  }
}

// ============================================================================
// VOLATILITY ALERT COOKIES
// ============================================================================

/**
 * Store volatility alert state with color coding
 */
export function setVolatilityAlertCookie(
  response: Response,
  metrics: {
    fd: number;
    vpin: number;
    whaleAlert: boolean;
    marketQuality: number;
  },
  options: Partial<CookieOptions> = {}
): void {
  const regimeColor = fdToColor(metrics.fd);
  const isCritical = metrics.fd > 2.3 || metrics.vpin > 0.7;
  
  const alert = {
    timestamp: Date.now(),
    fd: metrics.fd.toFixed(2),
    vpin: metrics.vpin.toFixed(3),
    whaleAlert: metrics.whaleAlert,
    marketQuality: metrics.marketQuality.toFixed(1),
    color: {
      hex: Bun.color(regimeColor, "hex") as string,
      HEX: Bun.color(regimeColor, "HEX") as string,
      ansi: Bun.color(regimeColor, "ansi") as string,
      css: Bun.color(regimeColor, "css") as string
    },
    severity: isCritical ? "CRITICAL" : metrics.fd > 1.9 ? "HIGH" : "MODERATE",
    requiresAction: metrics.whaleAlert || metrics.marketQuality < 50
  };
  
  const cookieValue = JSON.stringify(alert);
  
  const cookieOptions = {
    path: "/",
    maxAge: 60 * 5, // 5 minutes - very volatile
    httpOnly: false,
    sameSite: "lax",
    ...options
  };

  let cookie = `volatilityAlert=${cookieValue}`;
  if (cookieOptions.path) cookie += `; Path=${cookieOptions.path}`;
  if (cookieOptions.httpOnly) cookie += `; HttpOnly`;
  if (cookieOptions.maxAge) cookie += `; Max-Age=${cookieOptions.maxAge}`;
  if (cookieOptions.sameSite) cookie += `; SameSite=${cookieOptions.sameSite}`;
  if (cookieOptions.secure) cookie += `; Secure`;

  response.headers.append("Set-Cookie", cookie);
}

/**
 * Get volatility alert from cookies
 */
export function getVolatilityAlertCookie(
  request: Request
): any | null {
  const cookies = new Bun.CookieMap(request.headers.get("cookie") || "");
  const raw = cookies.get("volatilityAlert");

  if (!raw) return null;

  try {
    const alert = JSON.parse(raw);
    // Check if expired
    if (Date.now() - alert.timestamp > 5 * 60 * 1000) {
      return null;
    }
    return alert;
  } catch {
    return null;
  }
}

// ============================================================================
// SESSION TRACKING COOKIES
// ============================================================================

/**
 * Track session with color-coded state
 */
export function setSessionStateCookie(
  response: Response,
  session: {
    id: string;
    marketId: string;
    fd: number;
    active: boolean;
    startTime: number;
  },
  options: Partial<CookieOptions> = {}
): void {
  const stateColor = fdToColor(session.fd);

  const sessionData = {
    id: session.id,
    market: session.marketId,
    fd: session.fd.toFixed(2),
    active: session.active,
    start: session.startTime,
    duration: Date.now() - session.startTime,
    color: {
      HEX: Bun.color(stateColor, "HEX") as string,
      hsl: Bun.color(stateColor, "hsl") as string,
      lab: Bun.color(stateColor, "lab") as string
    },
    status: session.active ? "ACTIVE" : "ENDED"
  };

  const cookieValue = JSON.stringify(sessionData);

  const cookieOptions = {
    path: "/",
    maxAge: 60 * 60 * 2, // 2 hours
    httpOnly: false,
    secure: true,
    sameSite: "lax",
    ...options
  };

  let cookie = `sessionState=${cookieValue}`;
  if (cookieOptions.path) cookie += `; Path=${cookieOptions.path}`;
  if (cookieOptions.httpOnly) cookie += `; HttpOnly`;
  if (cookieOptions.maxAge) cookie += `; Max-Age=${cookieOptions.maxAge}`;
  if (cookieOptions.sameSite) cookie += `; SameSite=${cookieOptions.sameSite}`;
  if (cookieOptions.secure) cookie += `; Secure`;

  response.headers.append("Set-Cookie", cookie);
}

/**
 * Get session state from cookies
 */
export function getSessionStateCookie(
  request: Request
): any | null {
  const cookies = new Bun.CookieMap(request.headers.get("cookie") || "");
  const raw = cookies.get("sessionState");

  if (!raw) return null;

  try {
    return JSON.parse(raw);
  } catch {
    return null;
  }
}

